import React, { useState, useEffect } from "react";
import styled from "@emotion/styled";
import { Link, useLocation } from "react-router-dom";
import useLocalStorage from 'use-local-storage';
import axios from "axios";
import TableCoins from "../../components/TableCoins";

import { useWidth } from "../../context/ScreenWidthContext";

// Components
import ContactForm from "../../components/contact-form";
import PortfolioFeed from "../../components/portfolio";
import Testimonials from "../../components/testimonials";

// Backgrounds
import TimelineBG from "../../assets/images/bgs/timeline-bg.jpg";
import Dust4 from "../../assets/images/bgs/screen-dust-4.png";
import Grid from "../../assets/images/bgs/grid.png";

//Profile Image
import ProfileImage from "../../assets/images/new/bb-profile.jpg";

const HomeContainer = styled.div `
  display: flex;
  flex-direction: column;
  align-items: center;
  width: 100%;
  margin: 0 auto;
`;

const Button = styled(Link) `
  padding:1rem 1.2rem;
  border:3px solid #92ec50;
  border-radius:3px;
  font-family:'Classic Console';
  text-decoration:none;
  text-transform:uppercase;
  font-size: clamp(1rem, 4vw, 1.2rem);
  width:42%;
  text-align:center;
  display: flex;
  align-items: center;
  justify-content: center;
  :hover{
    cursor:pointer;
    transition:0.2s ease-in-out;
    box-shadow: inset 0px 0px 20px #87c763a3, 0 0 10px #87c76361, 0 0 25px #87c7634d, 0 0 35px #87c7634d, 0 0 45px #87c76312, 0 0 55px #87c76312, 0 0 65px #87c76312, 0 0 75px #87c76312;
  }
  :active{
    color: rgb(139 239 74);
  }
  :focus{
    color: rgb(139 239 74);
  }
  :visited{
    color: rgb(139 239 74);
  }
  :target{
    color: rgb(139 239 74);
  }
  :focus-visible{
    color: rgb(139 239 74);
  }
  @media(max-width:892px){
    width:65%;
  }
`;

// Sections

const Intro = styled.div `
    height:calc(20rem + 25 * ((100vw - 20rem) / 60));
    display:flex;
    align-items:center;
    justify-content:center;
    h1{
      width: 100%;
      text-align: center;
      animation: typing 2s steps(22), blink .5s step-end infinite alternate;
      white-space: nowrap;
      line-height:2;
      overflow: hidden;
      border-right: 3px solid;
      color: var(--text-primary);
      z-index: 22222;
      position: relative;
      text-shadow: 0 0 10px var(--accent), 0 0 25px var(--accent), 0 0 35px var(--accent), 0 0 45px var(--accent), 0 0 55px var(--accent), 0 0 65px var(--accent), 0 0 75px var(--accent);
    }
    span {
      font-family: 'Monofoto';
      font-weight: bold;
      font-size: clamp(1.722rem, 6vw, 7rem);
      z-index: 22222;
      position: relative;
      text-shadow: 0 0 10px #87c76361, 0 0 35px #87c7634d, 0 0 55px #87c76312, 0 0 75px #87c76312;
    }
    @media(max-width:892px){
      h1{
        line-height: 4;
      }
    }
`;

const api = "https://onw7s0vkl3.execute-api.us-east-1.amazonaws.com/clayton-fob-api-prod";

const Home = (match) => {

  const width = useWidth();
  const location = useLocation();

  const [isLoading, setIsLoading] = useState(true);
  const [data, setData] = useState();

  const [coins, setCoins] = useState([]);
  const [search, setSearch] = useState("");

  const ethPrice = 

  fetch('https://api.coingecko.com/api/v3/simple/price?ids=55&vs_currencies=eth',
    {
      method: 'GET',
      // body: JSON.stringify(VideoUpload),
      headers: {
        'Content-Type':'application/json'
      },
    }
  )

  useEffect(() => {
    fetch('https://api.coingecko.com/api/v3/simple/price?ids=55&vs_currencies=eth', {
      method: 'GET',
      headers: {
        'Content-Type':'application/json'
      },
    }
    )
      .then((res) => res.json())
      .then((response) => {
        setData(response);
        setIsLoading(false);
        console.log('https://api.coingecko.com/api/v3/simple/price?ids=55&vs_currencies=eth');
      })
      .catch((error) => console.log("error",error));
  }, []);

  useEffect(() => {
    fetch('https://api.coingecko.com/api/v3/ping', {
      method: 'GET',
      headers: {
        'Content-Type':'application/json'
      },
    }
    )
      .then((res) => res.json())
      .then((response) => {
        setData(response);
        setIsLoading(false);
        console.log('https://api.coingecko.com/api/v3/ping');
      })
      .catch((error) => console.log("error",error));
  }, []);

  return ( 

  <>

    <HomeContainer>

              <Intro>

              <input
                type="text"
                placeholder="Search a Coin"
                className="form-control bg-dark text-light border-0 mt-4 text-center"
                autoFocus
                onChange={(e) => setSearch(e.target.value)}
              />

              <TableCoins coins={coins} search={search} />

              <div>
              {!ethPrice && ( !data ? ( "Data" ) : ( data ))}
              {!isLoading && ( !data ? ( "Data" ) : ( data ))}
              </div>

              </Intro>

    </HomeContainer>

  </>

    );
};

export default Home;
